import React, {useEffect, useState} from "react";
import Input from "../../components/Input";


import './Register.css'

function Register(){

  const [id, setId] = useState('');
  const [pw, setPw] = useState('');
  const [pwcheck, setPwcheck] = useState('');
  const [name, setName] = useState('');
  const [phone, setPhone] = useState('');
  const [nickname, setNickname] = useState('');
  const [error, setError] = useState(false);
  const [errormsg, setErrormsg] = useState('');

  const onSubmit = () => {
    //axios.post //~
  }

  const idcheck = (data) => {
    if(data === '')
        return false;
  }

  useEffect(() => {
    console.log(error);

    if (
      id == ""
    ) {
      setError(true);
      setErrormsg("빈칸을 모두 채워주세요.");
    } else if (!idcheck(id)){
      setError(true);
      setErrormsg("id check 실패");
    }
  });

  return (
    <div className='main'>
      <div className='header2'>

      </div>
      
      <div className='register'>
        <Input
          distinct={"Short"}
          type={"text"}
          placeholder={"아이디"}
          value={id}
          setValue={setId}
        />
        <button className="overlap">중복 확인</button>
        <br/>
        <Input
          type={"password"}
          placeholder={"비밀번호"}
          value={pw}
          setValue={setPw}
        /><br/>
        <Input
          type={"password"}
          placeholder={"비밀번호 확인"}
          value={pwcheck}
          setValue={setPwcheck}
        /><br/>
        <Input
          type={"text"}
          placeholder={"이름"}
          value={name}
          setValue={setName}
        /><br/>
        <Input
          distinct={"Short"}
          type={"text"}
          placeholder={"휴대폰"}
          value={phone}
          setValue={setPhone}
        /><button className="overlap">중복 확인</button>
        <br/>
        <Input
          distinct={"Short"}
          type={"text"}
          placeholder={"닉네임"}
          value={nickname}
          setValue={setNickname}
        /><button className="overlap">중복 확인</button>
        <br/>
        {error ? <span className="error">{errormsg}</span> : <></>}
      </div>
    </div>
  )
}

export default Register;
