import React, {useEffect, useState} from "react";
import Input from "../../components/Input";
import SubmitBtn from "../../components/SubmitBtn";

import './Register.css'

function Register(){

  const [id, setId] = useState('');
  const [pw, setPw] = useState('');
  const [pwcheck, setPwcheck] = useState('');
  const [name, setName] = useState('');
  const [phone, setPhone] = useState('');
  const [nickname, setNickname] = useState('');
  const [error, setError] = useState(false);
  const [errormsg, setErrormsg] = useState('');

  const onSubmit = () => {
    //axios.post //~DB에 정보넣기~
  }

  const onIdOverlap = () => {
    //axios.post //~ 중복확인하기~
  }

  const onPhoneOverlap = () => {
    //axios.post //~ 중복확인하기~
  }

  const onNickOverlap = () => {
    //axios.post //~ 중복확인하기~
  }

  const idcheck = (data) => {
    if(data === '')
        return false;
    return true;
  }

  useEffect(() => {
    console.log(error);

    if (
      id === "" ||
      pw === "" ||
      pwcheck === "" ||
      name === "" ||
      phone === "" ||
      nickname === ""
    ) {
      setError(true);
      setErrormsg("모두 입력해주세요.");
    } else if (!idcheck(id)){
      setError(true);
      setErrormsg("id check 실패");
    } else {
      setError(false);
    }
  }, [error, id]);

  return (
    <div className='main'>
      <div className='header2'>

      </div>
      
      <div className='register'>
        <Input
          distinct={"Short"}
          type={"text"}
          placeholder={"아이디"}
          value={id}
          setValue={setId}
        />
        <button className="overlap" onClick={onIdOverlap}>중복 확인</button>
        <br/>
        <Input
          type={"password"}
          placeholder={"비밀번호"}
          value={pw}
          setValue={setPw}
        /><br/>
        <Input
          type={"password"}
          placeholder={"비밀번호 확인"}
          value={pwcheck}
          setValue={setPwcheck}
        /><br/>
        <Input
          type={"text"}
          placeholder={"이름"}
          value={name}
          setValue={setName}
        /><br/>
        <Input
          distinct={"Short"}
          type={"text"}
          placeholder={"휴대폰"}
          value={phone}
          setValue={setPhone}
        />
        <button className="overlap" onClick={onPhoneOverlap}>중복 확인</button>
        <br/>
        <Input
          distinct={"Short"}
          type={"text"}
          placeholder={"닉네임"}
          value={nickname}
          setValue={setNickname}
        />
        <button className="overlap" onClick={onNickOverlap}>중복 확인</button>
        <br/>
        <div className="errmsgbox">
          {error ? <span className="regerrormsg">{errormsg}</span> : <></>}
        </div>
        <br/>
        <SubmitBtn
         onClick={onSubmit}
         text={"제출하기"}
         disabled={error}
        />
      </div>
    </div>
  )
}

export default Register;
