import React, {useEffect, useState} from "react";
import Input from "../../components/Input";
import SubmitBtn from "../../components/SubmitBtn";

import './Register.css'

function Register(){

  const [id, setId] = useState('');
  const [pw, setPw] = useState('');
  const [pwconfirm, setPwconfirm] = useState('');
  const [name, setName] = useState('');
  const [phone, setPhone] = useState('');
  const [nickname, setNickname] = useState('');
  const [error, setError] = useState(false);
  const [errormsg, setErrormsg] = useState('');

  const [idoverlap, setIdoverlap] = useState(false);
  const [phoneoverlap, setPhoneoverlap] = useState(false);
  const [nickoverlap, setNickverlap] = useState(false);

  const onSubmit = () => {
    //axios.post //~DB에 정보넣기~
  }

  const onIdOverlap = () => {
    //axios.post //~ 중복확인하기~
    //성공 시 setIdoverlap(true);
  }

  const onPhoneOverlap = () => {
    //axios.post //~ 중복확인하기~
    //성공 시 setPhoneoverlap(true);
  }

  const onNickOverlap = () => {
    //axios.post //~ 중복확인하기~
    //성공 시 setNickoverlap(true);
  }

  const pwcheck = (data) => {
    //pw 조건 - 6자리 이상, 숫자와 문자 조합
    var regExp = /^(?=.*\d)(?=.*[a-zA-Z])[0-9a-zA-Z]{8,10}$/
    return regExp.test(data.value);
  }

  const phonecheck = (data) => {

  }

  useEffect(() => {
    console.log(error);

    if (
      id === "" ||
      pw === "" ||
      pwconfirm === "" ||
      name === "" ||
      phone === "" ||
      nickname === ""
    ) {
      setError(true);
      setErrormsg("모두 입력해주세요.");
    } else if (!pwcheck(pw)){
      setError(true);
      setErrormsg("비밀번호를 올바른 형식으로 입력해주세요.\n문자와 숫자 조합, 6자리 이상");
    } else if (!phonecheck(phone)){
      setError(true);
      setErrormsg("휴대폰 번호를 올바른 형식으로 입력해주세요.\nex)010-1234-5678")
    } else {
      setError(false);
    }
  }, [error, id]);

  return (
    <div className='main'>
      <div className='header2'>

      </div>
      
      <div className='register'>
        <Input
          distinct={"Short"}
          type={"text"}
          placeholder={"아이디"}
          value={id}
          setValue={setId}
        />
        <button className="overlap" onClick={onIdOverlap}>중복 확인</button>
        <br/>
        <Input
          type={"password"}
          placeholder={"비밀번호 (문자와 숫자 조합, 6자리 이상)"}
          value={pw}
          setValue={setPw}
        /><br/>
        <Input
          type={"password"}
          placeholder={"비밀번호 확인"}
          value={pwconfirm}
          setValue={setPwconfirm}
        /><br/>
        <Input
          type={"text"}
          placeholder={"이름"}
          value={name}
          setValue={setName}
        /><br/>
        <Input
          distinct={"Short"}
          type={"text"}
          placeholder={"휴대폰 (010-1234-5678)"}
          value={phone}
          setValue={setPhone}
        />
        <button className="overlap" onClick={onPhoneOverlap}>중복 확인</button>
        <br/>
        <Input
          distinct={"Short"}
          type={"text"}
          placeholder={"닉네임"}
          value={nickname}
          setValue={setNickname}
        />
        <button className="overlap" onClick={onNickOverlap}>중복 확인</button>
        <br/>
        <div className="errmsgbox">
          {error ? <span className="regerrormsg">{errormsg}</span> : <></>}
        </div>
        <br/>
        <SubmitBtn
         onClick={onSubmit}
         text={"제출하기"}
         disabled={error}
        />
      </div>
    </div>
  )
}

export default Register;
